INSERT INTO `blog` (`id_blog`, `id_loaiblog`, `name_blog`, `tomtat_blog`, `noidung_blog`, `time_blog`, `luotxem`, `id`, `img_blog`, `slug_blog`, `Anhien`, `noibat`, `tag_blog`, `created_at`, `updated_at`) VALUES
(1, 4, 'Mẹ bầu có nên dùng băng vệ sinh hằng ngày không?', '<p>“Có nên dùng băng vệ sinh hàng ngày không” là băn khoăn đang nhận được nhiều tranh cãi của hội chị em. Phần đông ý kiến cho rằng, BVS hằng ngày giúp vùng kín sạch, khô thoáng, không ẩm ướt và đặc biệt tiện lợi khi đi làm cả ngày, chỉ cần sử dụng đúng cách thì vẫn an toàn. Một số lại phản đối, việc sử dụng BVS hằng ngày khi mang thai không tốt. Vậy thực hư là như thế nào?<br></p>', '<div class=\"block-detail\" style=\"border-bottom: 1px solid rgb(207, 222, 186); margin-bottom: 25px; padding-bottom: 17px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><h2 class=\"block-detail-ttl\" style=\"font-family: latobold; font-weight: 500; color: inherit; margin: 13px 0px; font-size: 26px;\"><span style=\"background-color: rgb(255, 255, 255); font-family: KoHo;\">Mẹ bầu có nên dùng băng vệ sinh hằng ngày không?</span></h2><h3 class=\"block-detail-description\" style=\"font-family: latobold; font-weight: 500; line-height: 1.3; color: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; font-size: 18px;\"></h3></div><div class=\"block-detail-content\" style=\"border-bottom: 1px solid rgb(207, 222, 186); margin-bottom: 10px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">“Có nên dùng băng vệ sinh hàng ngày không” là băn khoăn đang nhận được nhiều tranh cãi của hội chị em. Phần đông ý kiến cho rằng, BVS hằng ngày giúp vùng kín sạch, khô thoáng, không ẩm ướt và đặc biệt tiện lợi khi đi làm cả ngày, chỉ cần sử dụng đúng cách thì vẫn an toàn. Một số lại phản đối, việc sử dụng BVS hằng ngày khi mang thai không tốt. Vậy thực hư là như thế nào?</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">I/ Có nên dùng băng vệ sinh hàng ngày khi mang thai không?</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255);\"><span style=\"font-family: KoHo;\">Có.&nbsp;</span><a href=\"https://www.organica.vn/cham-soc-co-the/bvs-hang-ngay-natracare-16pcs\" style=\"color: rgb(51, 51, 51); outline: none !important;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-weight: 700; font-family: KoHo;\"><em>Băng vệ sinh hằng ngày</em></span></span></a><span style=\"font-family: KoHo;\">&nbsp;sẽ là giải pháp “cứu nguy” để vùng kín mẹ bầu không rơi vào tình trạng ẩm ướt khi khí hư ra nhiều (môi trường lý tưởng để vi khuẩn sinh sôi, phát triển gây viêm nhiễm âm đạo), không còn mùi hôi khó chịu, không ngứa ngáy và đặc biệt là tiện lợi khi đi làm cả ngày mà vùng kín vẫn khô thoáng và sạch sẽ.</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/May/5/5555-643731588643869-1588643869.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Tuy nhiên, để đảm bảo an toàn cho vùng kín thì mẹ cần biết lựa chọn và sử dụng băng vệ sinh hằng ngày đúng cách. Cụ thể, mẹ cần nắm được các nguyên tắc cơ bản sau.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Chọn BVS hữu cơ thay vì băng vệ sinh thường</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Bạn có biết, hóa chất từ những sản phẩm tiếp xúc với bề mặt da có thể thẩm thấu qua các mạch máu đi vào cơ thể? Đó là lý do thay vì khẳng định chung chung rằng mẹ bầu nên tìm mua các dòng BVS chất lượng thì chúng tôi khuyến khích chị em nên lựa chọn BVS hữu cơ - dòng BVS có chứng nhận an toàn rõ ràng và đáng tin cậy nhất hiện nay.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Cụ thể BVS hữu cơ giúp mẹ an tâm về mức độ an toàn khi không có sự can thiệp của hóa chất, nhựa, chất tạo mùi, hương liệu, thuốc tẩy… Bên cạnh đó, vì được làm từ 100% bông hữu cơ, khả năng thấm hút tốt nên khi sử dụng BVS hữu cơ vùng kín chị em sẽ luôn thông thoáng, cảm giác mềm mại, vô cùng dễ chịu cho “cô bé”.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Thay BVS 4 tiếng/lần</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">BVS hàng ngày được thiết kế nhỏ gọn với khả năng thấm hút không quá cao như BVS ban ngày hay ban đêm. Thế nhưng, điều này không có nghĩa bạn có thể sử dụng chúng cả ngày mà không thay; bởi dẫu BVS có chất lượng thì môi trường ẩm ướt tồn tại lâu thì vi khuẩn hoàn toàn có thể tự sản sinh. Thế nên, đừng quên nguyên tắc tuyệt đối quan trọng khi sử dụng BVS đó là thay 4 tiếng/lần dù lượng khí hư tiết ra ít.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Không lạm dụng BVS hằng ngày</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">BVS hằng ngày đúng là “cứu tinh” cho nhiều chị em trong rất nhiều trường hợp, nhất là khi khí hư ra nhiều trong thời gian mang thai. Tuy nhiên, hãy chỉ sử dụng chúng khi thấy cần thiết, không nên lạm dụng.&nbsp;</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">II/ 5 lưu ý quan trọng mẹ cần biết để giữ vùng kín khỏe mạnh.</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Ngoài việc học cách lựa chọn và sử dụng BVS hằng ngày thì mẹ bầu cũng cần nắm những lưu ý tối quan trọng để giữ gìn vùng kín khỏe mạnh trong thời gian mang thai.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/May/5/5554-469741588643868-1588643869.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Lựa chọn quần lót bằng cotton, rộng rãi</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Hãy ưu tiên các loại quần lót bằng chất liệu cotton, rộng rãi, thoải mái, tránh sử dụng quần áo bó sát, chất chội không tốt cho vùng kín.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Vệ sinh vùng kín sạch sẽ hằng ngày</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">BVS không thể thay thế bạn vệ sinh vùng kín, thế nên mẹ đừng quên vệ sinh vùng kín bằng nước ấm mỗi tối trước khi đi ngủ, sau khi quan hệ và sau khi ngủ dậy nhé. Hạn chế sử dụng các dung dịch vệ sinh có chứa xà phòng.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Tránh thụt rửa khi vệ sinh âm đạo</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Việc thụt rửa sẽ làm xáo trộn cân bằng tự nhiên của vi khuẩn trong âm đạo, làm tăng nguy cơ nhiễm trùng, viêm âm đạo.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Không tắm bồn</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Việc tắm bồn có thể giúp mẹ dễ chịu hơn. Tuy nhiên, đối với phụ nữ mang thai, tắm bồn làm tăng nguy cơ gây kích ứng, ngứa vùng kín.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Gặp bác sĩ khi vùng kín có dấu hiệu bất thường</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Mặc dù ra khí hư nhiều khi mang thai là điều bình thường. Tuy nhiên nếu khí hư có mùi hôi khó chịu, màu vàng, xanh, nâu… thì mẹ cần đến gặp bác sĩ ngay để được chẩn đoán và điều trị nếu có vấn đề.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Kết luận: Khi mang thai, khi hư ra nhiều mẹ bầu hoàn toàn có thể sử dụng băng vệ sinh hằng ngày để vùng kín luôn khô thoáng và sạch sẽ. Tuy nhiên, mẹ cần lựa chọn dòng BVS an toàn và sử dụng đúng cách cũng như kết hợp các biện pháp vệ sinh vùng kín khoa học.&nbsp;</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255);\"><span style=\"font-family: KoHo;\">Hy vọng với những chia sẻ trên đã phần nào giúp các mẹ bầu có đáp án cho băn khoăn “Có nên dùng băng vệ sinh hằng ngày khi mang thai không? Nếu có nhu cầu tìm mua BVS hữu cơ, mẹ bầu có thể tìm đến các cửa hàng hữu cơ của Organica, đặt hàng qua website, fanpage hoặc gọi ngay vào Hotline&nbsp;</span><span style=\"font-weight: 700; font-family: KoHo;\">0901 828 689</span><span style=\"font-family: KoHo;\">&nbsp;để được tư vấn và hỗ trợ chi tiết nhất.</span></span></p></div>', '2020-11-15', '12', 2, '5557-180151588643884-1588643884.jpg', 'me-bau-co-nen-dung-bang-ve-sinh-hang-ngay-khong', 1, 0, 'dinh dưỡng cho mẹ bầu, bé ăn dặm, cùng con khôn lớn, 6 tháng đầu tiên', '2020-10-30 10:55:50', '2020-11-15 07:00:08'),
(2, 4, 'Mẹ bầu ăn gì để tăng cường sức đề kháng mùa dịch bệnh?', '<p>Mang thai, hệ miễn dịch của người mẹ bị suy giảm trầm trọng, cơ thể dễ bị tấn công bởi virus, tiềm ẩn các căn bệnh nguy hiểm, đặc biệt là trước tình hình dịch bệnh diễn biến phức tạp như hiện nay. Vậy nên, bên cạnh các biện pháp bảo vệ từ bên ngoài, mẹ cần chủ động xây dựng cho mình chế độ dinh dưỡng hợp lý nhằm tăng cường sức đề kháng, giảm nguy cơ mắc bệnh.<br></p>', '<div class=\"block-detail\" style=\"border-bottom: 1px solid rgb(207, 222, 186); margin-bottom: 25px; padding-bottom: 17px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><h2 class=\"block-detail-ttl\" style=\"font-family: latobold; font-weight: 500; color: inherit; margin: 13px 0px; font-size: 26px;\"><span style=\"background-color: rgb(255, 255, 255); font-family: KoHo;\">Mẹ bầu ăn gì để tăng cường sức đề kháng mùa dịch bệnh?</span></h2><h3 class=\"block-detail-description\" style=\"font-family: latobold; font-weight: 500; line-height: 1.3; color: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; font-size: 18px;\"><span style=\"background-color: rgb(255, 255, 255); font-family: KoHo;\">Mang thai, hệ miễn dịch của người mẹ bị suy giảm trầm trọng, cơ thể dễ bị tấn công bởi virus, tiềm ẩn các căn bệnh nguy hiểm, đặc biệt là trước tình hình dịch bệnh diễn biến phức tạp như hiện nay. Vậy nên, bên cạnh các biện pháp bảo vệ từ bên ngoài, mẹ cần chủ động xây dựng cho mình chế độ dinh dưỡng hợp lý nhằm tăng cường sức đề kháng, giảm nguy cơ mắc bệnh.</span></h3></div><div class=\"block-detail-content\" style=\"border-bottom: 1px solid rgb(207, 222, 186); margin-bottom: 10px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Dưới đây là những thực phẩm có tác dụng tăng cường sức đề kháng mẹ nên bổ sung vào thực đơn ăn uống hằng ngày của mình.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/April/3/5359-296021585889564-1585889564.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%; float: none;\"></span></span></p><h2 style=\"font-family: inherit; font-weight: 500; line-height: 1.1; color: inherit; margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">1/ Thực phẩm giàu protein</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Protein là dưỡng chất thiết yếu cung cấp năng lượng cho cơ thể, khi mang thai nhu cầu năng lượng của mẹ bầu càng tăng cao vì phải cung cấp các dưỡng chất cho sự phát triển của thai nhi. Bên cạnh đó, protein cũng đóng vai trò tạo ra các kháng thể cho cơ thể, tăng cường sức đề kháng. Vậy nên, chế độ dinh dưỡng trong mùa dịch mẹ đừng quên bổ sung các nhóm thực phẩm giàu protein.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Các nguồn thực phẩm cung cấp hàm lượng protein dồi dào mẹ bầu nên bổ sung gồm:</span></p><ul style=\"margin-bottom: 10px; text-align: justify;\"><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Đậu (đen, đỏ, xanh, đậu lăng… cung cấp 40g protein/100g)</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Quả bơ (40g protein/100g)</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Thịt bò nạc (36g protein/100g)</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Bí ngô, hạt bí (33g protein/100g)</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Thịt gà/lợn nạc (30g protein/100g)</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Hạt hướng dương (20g protein/100g)</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Tảo xanh, tảo xoắn, đậu phộng, tôm, cua</span></li></ul><h2 style=\"font-family: inherit; font-weight: 500; line-height: 1.1; color: inherit; margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">2/ Thực phẩm giàu vitamin C</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Vitamin C là chất chống oxy hóa cao, có tác dụng tăng cường sức đề kháng hiệu quả, chẳng những thế vitamin C còn giúp mẹ bầu sở hữu một làn da sáng, mềm mịn, giảm thâm nám. Thế nên, thực đơn hằng ngày mẹ đừng quên bổ sung vitamin C, nhất là trong thời điểm dịch bệnh diễn biến phức tạp như hiện nay.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/April/3/5360-573271585889565-1585889565.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Những thực phẩm giàu vitamin C mẹ bầu cần cho vào danh sách dinh dưỡng gồm:</span></p><ul style=\"margin-bottom: 10px; text-align: justify;\"><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Trái cây: cam, quýt, bưởi, chanh, dâu tây, táo, đào, mơ, hồng…</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau củ quả: Cà chua, ớt chuông, củ cải đường, khoai tây bắp cải, rau bina</span></li></ul><h2 style=\"font-family: inherit; font-weight: 500; line-height: 1.1; color: inherit; margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">3/ Thực phẩm giàu vitamin D</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Vitamin D giúp tăng cường khả năng chống vi sinh gây bệnh của tế bào miễn dịch, tăng cường phản ứng miễn dịch. Rất nhiều nghiên cứu cho thấy vitamin giúp giảm nguy cơ viêm phổi, lo âu… Vậy nên, mẹ bầu đừng quên bổ sung nguồn cung cấp vitamin D, đặc biệt là qua thực phẩm như:</span></p><ul style=\"margin-bottom: 10px; text-align: justify;\"><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Cá hồi, cá trích, cá mòi, cá tuyết, cá ngừ</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Dầu gan cá tuyết</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Hàu, tôm</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Lòng đỏ trứng</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Nấm</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Nước cam</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Sữa đậu nành, sữa bò</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Ngũ cốc, yến mạch</span></li></ul><h2 style=\"font-family: inherit; font-weight: 500; line-height: 1.1; color: inherit; margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">4/ Tỏi đen</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Tỏi đen rất tốt cho phụ nữ mang thai. Chúng có tác dụng điều chỉnh hệ tiêu hóa, cải thiện giấc ngủ. Đặc biệt là tăng cường khả năng miễn dịch và giảm nguy cơ tăng huyết áp (tình trạng thường xảy ra ở phụ nữ mang thai). Ngoài ra, mẹ cũng yên tâm khi sử dụng vì sau quá trình lên men, tỏi không còn mùi hăng khó chịu. Mỗi ngày mẹ có thể dùng từ 1-2 củ tỏi đen để tăng cường sức đề kháng.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255);\"><span style=\"font-family: KoHo;\">Tham khảo&nbsp;</span><span style=\"font-weight: 700; font-family: KoHo;\"><a href=\"https://www.organica.vn/san-pham-che-bien/toi-den-huu-co-han-quoc-nhs-100g\" style=\"color: rgb(51, 51, 51); outline: none !important;\">tỏi đen hữu cơ</a></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/April/3/5361-977091585889565-1585889565.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><h2 style=\"font-family: inherit; font-weight: 500; line-height: 1.1; color: inherit; margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">5/ Thực phẩm giàu sắt</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Sắt đóng vai trò vô cùng quan trọng trong chế độ dinh dưỡng của phụ nữ mang thai, không chỉ giúp ngăn ngừa tình trạng thiếu máu khi mang thai mà sắt còn hỗ trợ tăng cường sức đề kháng cho cơ thể hiệu quả. Ngoài sử dụng viên uống sắt, mẹ đừng quên bổ sung các thực phẩm giàu sắt sau vào thực đơn hằng ngày của mình, nhất là trong mùa dịch bệnh:</span></p><ul style=\"margin-bottom: 10px; text-align: justify;\"><li><span style=\"font-weight: 700;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\"><a href=\"https://www.organica.vn/product?keyword=h%E1%BA%A1t+%C4%91i%E1%BB%81u\" style=\"color: rgb(51, 51, 51); outline: none !important;\">Hạt điều</a></span></span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Đậu nành</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau bina</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Củ cải đỏ</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Dưa hấu</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Lựu&nbsp;</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Ức gà</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Táo</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Cá thu, cá hồi</span></li><li><span style=\"font-weight: 700;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\"><a href=\"https://www.organica.vn/ngu-coc-dau-va-hat/hat-bi-tach-vo-huu-co\" style=\"color: rgb(51, 51, 51); outline: none !important;\">Hạt bí</a></span></span></li></ul><h2 style=\"font-family: inherit; font-weight: 500; line-height: 1.1; color: inherit; margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">6/ Thực phẩm giàu kẽm</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Kẽm đóng vai trò đặc biệt quan trọng đối với hệ miễn dịch của cơ thể, chúng giúp tăng chức năng của các tế bào bạch cầu, từ đó nhận diện và tiêu diệt các tế bào lạ xâm nhập vào cơ thể. Kẽm cũng cực kỳ quan trọng với bà bầu, chúng giúp mẹ không bị mệt mỏi, giảm nguy cơ mắc các bệnh cảm, cúm. Một số thực phẩm giàu kẽm mẹ bầu cần chú ý bổ sung như:</span></p><ul style=\"margin-bottom: 10px; text-align: justify;\"><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Hàu, sò</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Thịt đỏ</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau muống</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Củ cải</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Đậu, đỗ</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Lòng đỏ trứng gà</span></li></ul><h2 style=\"font-family: inherit; font-weight: 500; line-height: 1.1; color: inherit; margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">7/ Thực phẩm chứa nhiều lợi khuẩn</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Bổ sung lợi khuẩn là cách hiệu quả để tăng cường hệ miễn dịch khi mang thai, nhất là trong thời điểm tình hình dịch bệnh diễn biến phức tạp như hiện nay. Các thực phẩm chứa nhiều lợi khuẩn có thể kể đến như sữa chua, sữa yến mạch, bột yến mạch...</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Ngoài việc bổ sung các dưỡng chất có vai trò tăng đề kháng, xây dựng chế độ dinh dưỡng khoa học thì mẹ bầu cũng cần chú ý duy trì cho mình thói quen sinh hoạt lành mạnh như uống nhiều nước, vận động nhẹ nhàng mỗi ngày, ngủ đúng giờ đủ giấc. Đặc biệt là duy trì cho bản thân một tâm thế thoải mái nhất, không nên quá lo lắng, chỉ cần chú ý các biện pháp bảo vệ bản thân, hạn chế ra ngoài thì nguy cơ nhiễm bệnh gần như không có.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; text-align: justify;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Hy vọng với những chia sẻ trên đã phần nào mang đến những kiến thức dinh dưỡng hữu ích cho các mẹ bầu. Dịch bệnh vẫn đang diễn biến phức tạp và chưa có dấu hiệu hạ nhiệt. Chính vì vậy, qua đây Organica cũng muốn nhắn gửi đến tất cả mọi người hãy cố gắng duy trì các biện pháp bảo vệ bản thân, xây dựng chế độ dinh dưỡng hợp lý để cùng nhau vượt qua đại dịch này nhé.&nbsp;</span></p></div>', '2020-11-17', '22', 2, '5362-936811585889681-1585889681.jpg', 'me-bau-an-gi-de-tang-cuong-suc-de-khang-mua-dich-benh', 1, 1, 'mẹ bầu, bé ăn dặm', '2020-10-30 10:57:23', '2020-11-16 23:34:55'),
(3, 3, '7 cách để giữ rau, củ, quả hữu cơ tươi ngon lâu hơn', '<p>Rau củ quả hữu cơ được chị em nội trợ tin dùng vì yếu tố an toàn cao, đảm bảo cho sức khỏe cả gia đình, chưa kể hương vị tự nhiên để những bữa cơm thêm thơm ngon, hấp dẫn. Tuy nhiên, vì không can thiệp hóa chất, chất bảo quản nên rau củ quả hữu cơ thường có “hạn sử dụng” ngắn hơn rau củ quả thường.<br></p>', '<p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau củ quả hữu cơ được chị em nội trợ tin dùng vì yếu tố an toàn cao, đảm bảo cho sức khỏe cả gia đình, chưa kể hương vị tự nhiên để những bữa cơm thêm thơm ngon, hấp dẫn. Tuy nhiên, vì không can thiệp hóa chất, chất bảo quản nên rau củ quả hữu cơ thường có “hạn sử dụng” ngắn hơn rau củ quả thường.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/October/19/6840-455681603092741-1603092741.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Để thưởng thức rau củ quả tươi ngon, Organica luôn khuyến khích chị em nên mua rau củ quả vừa đủ dùng cho 2-3 ngày, không nên mua nhiều. Tuy nhiên, trường hợp nếu chị em đã lỡ mua và muốn rau củ quả vẫn tươi ngon thì có thể tham khảo một vài mẹo khoa học sau.</span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">I/ Rau củ quả hữu cơ “hạn sử dụng” ngắn. Làm sao để giữ độ tươi ngon nhất?</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Một số khách hàng thường thắc mắc rằng “sao chị thấy rau củ quả trên kệ nhà Organica luôn tươi ngon?, chẳng khi nào thấy rau héo úa, vàng”.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/October/19/6841-730441603092745-1603092745.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Thực ra, nhà Organica chẳng sử dụng “chiêu trò” gì đâu. Để rau củ quả đến tay người tiêu dùng luôn tươi ngon, đó là cả một hành trình nỗ lực của nhà O trong các khâu mà ít người biết được. Cụ thể như sau.</span></p><ul style=\"margin-bottom: 10px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau củ quả nhà Organica khi đến vụ thu hoạch sẽ được cắt, hái vào buổi chiều mát. Điều này giúp rau củ quả giữ được độ tươi nhất.</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Sau đó, rau củ quả được chọn lọc, loại bỏ những sản phẩm bầm dập, lá vàng, trái sâu… để đóng gói.&nbsp;</span></li><li><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau củ quả được vận chuyển nhanh chóng đến các cửa hàng để sáng đã có sẵn trên kệ phục vụ người mua.</span></li></ul><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Đặc biệt, Organica không thu hoạch ồ ạt rau củ quả, lượng rau củ quả thu về thường phục vụ hết trong 2-3 ngày, không có rau tồn dư nên rau củ quả mới luôn cập nhật trên kệ. Đó là lý do, rau củ quả trên kệ nhà Organica không chỉ cam kết an toàn chuẩn hữu cơ mà còn luôn tươi ngon nhất để phục vụ bữa cơm của anh/chị khách hàng ngon, giàu dinh dưỡng nhất.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><em><span style=\"font-size: 16px; background-color: rgb(255, 255, 255);\"><span style=\"font-family: KoHo;\">&gt;&gt;&gt; Tham khảo:&nbsp;</span><span style=\"font-weight: 700;\"><span style=\"color: rgb(153, 204, 0); font-family: KoHo;\"><a href=\"https://www.organica.vn/news/3/chung-nhan-huu-co-usda-co-y-nghia-gi\" style=\"color: rgb(153, 204, 0); outline: none !important;\">Chứng nhận hữu cơ USDA có ý nghĩa gì?</a></span></span></span></em></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">II/ Hướng dẫn bảo quản rau củ quả hữu cơ tươi ngon</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Để thưởng thức rau củ quả tươi ngon, Organica luôn khuyến khích chị em nên mua rau củ quả vừa đủ dùng cho 2-3 ngày, không nên mua nhiều. Tuy nhiên, trường hợp nếu chị em đã lỡ mua và muốn rau củ quả vẫn tươi ngon thì có thể tham khảo một vài mẹo khoa học sau.</span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Gói rau củ quả sinh khí Ethylene/nhạy cảm khí Ethylene trong túi giấy</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Ethylene là một loại hormone thực vật được tạo ra trong quá trình phát triển của cây trồng, đặc biệt ở giai đoạn chín. Đặc biệt, một số loại trái cây và rau quả sau khi thu hoạch sẽ tiếp tục sản sinh ra khí ethylene - khí này sẽ thúc đẩy củ quả nhanh chín, tăng quá trình vàng lá ở rau.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/October/19/6838-853641603092740-1603092740.jpg\" alt=\"\" width=\"700\" height=\"466\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Do đó, khi bảo quản nên tránh để chung các loại rau củ quả sinh khí ethylene với các loại rau củ quả nhạy cảm với khí ethylene. Để rau củ quả không nhanh chín, vàng úa thì bạn gói rau củ quả sinh khí Ethylene hoặc rau củ quả nhạy cảm với khí Ethylene vào túi giấy riêng.</span></p><table style=\"border-spacing: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; width: 686.6px;\"><tbody><tr><td style=\"padding: 0px; width: 310px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau củ quả sinh khí Ethylene</span></span></p></td><td style=\"padding: 0px; width: 362.6px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau củ quả nhạy cảm với khí Ethylene</span></span></p></td></tr><tr><td style=\"padding: 0px; width: 310px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Táo</span></p></td><td style=\"padding: 0px; width: 362.6px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Trái xoài</span></p></td></tr><tr><td style=\"padding: 0px; width: 310px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Dưa lưới</span></p></td><td style=\"padding: 0px; width: 362.6px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Măng tây</span></p></td></tr><tr><td style=\"padding: 0px; width: 310px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Trái bơ</span></p></td><td style=\"padding: 0px; width: 362.6px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Trái đào</span></p></td></tr><tr><td style=\"padding: 0px; width: 310px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Lê</span></p></td><td style=\"padding: 0px; width: 362.6px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Hành củ</span></p></td></tr><tr><td style=\"padding: 0px; width: 310px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Cà chua</span></p></td><td style=\"padding: 0px; width: 362.6px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Cà tím</span></p></td></tr><tr><td style=\"padding: 0px; width: 310px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Tiêu</span></p></td><td style=\"padding: 0px; width: 362.6px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Nho</span></p></td></tr><tr><td style=\"padding: 0px; width: 310px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Chuối chín</span></p></td><td style=\"padding: 0px; width: 362.6px;\"><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Dưa chuột/dưa leo</span></p></td></tr></tbody></table><h3 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 24px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Rau bina, xà lách Romaine, rau diếp.. Nên rửa trước khi cho vào tủ lạnh</span></span></h3><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/October/19/6837-884571603092740-1603092740.jpg\" alt=\"\" width=\"700\" height=\"466\" style=\"border: 0px; max-width: 100%;\"></span></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Không phải rau nào sau khi mua về chị em cũng nên rửa sạch rồi cho vào tủ lạnh. Nhưng với xà lách Romaine, rau diếp, rau bina thì chị em nên nhặt sạch, bỏ lá vàng, sau đó rửa để ráo sau đó cho vào tủ lạnh bảo quản (túi nhựa hoặc hộp đựng). Cách này giúp rau tươi ngon và giữ chất dinh dưỡng tốt.</span></p><h3 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 24px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Cắt cuống cà rốt đi</span></span></h3><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Khi mua cà rốt về nếu còn cuống xanh, chị em nên cắt bỏ đi trước khi bảo quản vì cuống sẽ hút chất dinh dưỡng có trong cà rốt. Có thể bảo quản cà rốt bên ngoài, nếu đã sơ chế thì nên bảo quản trong túi/hộp đậy kín ở ngăn mát tủ lạnh sẽ giữ được vài tuần.</span></p><h3 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 24px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Bảo quản chuối trong tủ lạnh đúng cách</span></span></h3><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Để giữ chuối được lâu (1 tuần), bạn nên tránh để chuối gần ánh nắng trực tiếp của mặt trời hoặc nơi có nhiều độ ẩm. Nếu bảo quản trong tủ lạnh thì tốt nhất cho vào túi zip, hút chân không rồi cho vào ngăn mát. Nếu không thì dùng màng thực phẩm bọc kín hoặc dùng túi giấy bọc kín, nên tách thành từng quả thay vì để cả nải chuối.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Lưu ý, nên ưu tiên mua chuối vừa chín, còn cứng, tránh mua chuối quá mềm. Đặc biệt chuối sau lấy ra từ tủ lạnh bạn nên ăn ngày, tránh để lâu, chuối sẽ bị thâm đen, không còn ngon.</span></p><h3 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 24px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Bọc giấy bạc cho cần tây</span></span></h3><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/October/19/6839-120191603092740-1603092740.jpg\" alt=\"\" width=\"700\" height=\"466\" style=\"border: 0px; max-width: 100%;\"></span></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Cần tây chứa nhiều nước, khá nhạy cảm với ethylene. Để giữ cần tây lâu trong tủ mà không bị mềm thì chị em nên bọc bằng giấy bạc rồi để trong ngăn kéo tủ lạnh, cách này sẽ giúp cần tây giữ được trong vài tuần.</span></p><h3 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 24px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Cho chanh vào túi</span></span></h3><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/October/19/6835-906591603092740-1603092740.jpg\" alt=\"\" width=\"700\" height=\"466\" style=\"border: 0px; max-width: 100%;\"></span></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Muốn chanh vàng để được lâu tối đa 1 tháng, chị em nên để chanh vào túi nhựa, hút hết không khí rồi cho vào tủ lạnh. Nếu không bọc chanh, chanh sẽ dễ bị khô, nhanh hỏng.</span></p><h3 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 24px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; background-color: rgb(255, 255, 255); font-family: KoHo;\">Nấm cần được thông gió</span></span></h3><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; background-color: rgb(255, 255, 255); font-family: KoHo;\">Đặc điểm của nấm là thích được giữ mát và thông gió tốt. Do đó, sau khi mua về, bạn đừng rửa hãy dùng khăn mềm lau nhẹ để loại bỏ bụi bẩn, đất cát nếu có sau đó xếp nấm vào giấy báo, gói lại để nơi khô ráo là được, chú ý nên gói gọn nhưng để hở ở trên. Nếu muốn bảo quản nấm trong 1 tháng thì cần làm sạch phần chân, gọt vỏ, cắt nhỏ rồi cho vào túi, hút hết không khí, kéo khóa và cho vào tủ đông.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\" style=\"background-color: rgb(255, 255, 255);\"><img src=\"https://tpnk2hhcxd.vcdn.com.vn/uploads/filecloud/2020/October/19/6836-743091603092740-1603092740.jpg\" alt=\"\" width=\"700\" height=\"466\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; font-family: KoHo;\">Mỗi loại rau củ quả sẽ có những đặc điểm riêng, chỉ khi hiểu tính chất của chúng, bạn mới bảo quản đúng cách. Song nếu được, Organica luôn khuyến khích người mua nên mua số lượng vừa đủ để thưởng thức hương vị rau củ quả tươi ngon nhất.</span><br><span style=\"font-size: 16px;\"><span style=\"font-family: KoHo;\">Khi mua thực phẩm tại cửa hàng, Organica luôn tư vấn cách sử dụng và bảo quản để đảm bảo dinh dưỡng và độ tươi ngon cao nhất.Hoặc nếu có thắc mắc, anh/chị đừng quên Hotline nhà Organica&nbsp;</span><span style=\"font-weight: 700; font-family: KoHo;\">0901 828 689&nbsp;</span><span style=\"font-family: KoHo;\">luôn túc trực để phục vụ.</span></span><span style=\"font-family: KoHo;\">﻿</span></span></p>', '2020-11-15', '15', 2, '6832-274221603092271-1603092271.jpg', '7-cach-de-giu-rau-cu-qua-huu-co-tuoi-ngon-lau-hon', 1, 1, 'rau củ quả, bảo quản rau củ quả', '2020-10-30 20:31:34', '2020-11-15 06:47:17');
INSERT INTO `blog` (`id_blog`, `id_loaiblog`, `name_blog`, `tomtat_blog`, `noidung_blog`, `time_blog`, `luotxem`, `id`, `img_blog`, `slug_blog`, `Anhien`, `noibat`, `tag_blog`, `created_at`, `updated_at`) VALUES
(4, 3, '9 thói quen xấu làm tăng nguy cơ mắc bệnh tiểu đường', '<p><span style=\"color: rgb(51, 51, 51); font-family: KoHo; font-size: 16px;\">Bệnh tiểu đường ngày càng có xu hướng trẻ hóa, nguyên nhân lại bắt nguồn từ chính những thói quen xấu hằng ngày mà nhiều người trẻ đang mắc phải nhưng không hề hay biết.</span><br></p>', '<p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Bệnh tiểu đường ngày càng có xu hướng trẻ hóa, nguyên nhân lại bắt nguồn từ chính những thói quen xấu hằng ngày mà nhiều người trẻ đang mắc phải nhưng không hề hay biết.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\"><img src=\"https://y5kbp0ifnvobj.vcdn.cloud/uploads/filecloud/2020/November/30/7163-814981606718687-1606718687.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Uống nhiều thức uống ngọt</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Rất nhiều bạn trẻ hiện nay lạm dụng nước ngọt, nước trái cây, trà sữa… những thức uống có lượng đường cực kỳ cao. Các chuyên gia cho rằng thực tế các loại nước ngọt công nghiệp gần như không có giá trị dinh dưỡng và không tạo cảm giác no, nó chỉ giúp bạn giải khát đồng thời nạp vào cơ thể lượng đường lớn, làm tăng nguy cơ mắc bệnh tiểu đường.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\"><img src=\"https://y5kbp0ifnvobj.vcdn.cloud/uploads/filecloud/2020/November/30/7162-199491606718687-1606718687.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-weight: 700;\"><span style=\"font-family: KoHo;\">Giải pháp:</span><span style=\"font-family: KoHo;\">&nbsp;</span></span></span><span style=\"font-family: KoHo;\">Nếu bạn là fan nước ngọt, trà sữa thì nên cắt giảm, thay thế bằng các loại nước ép, trà xanh, đồng thời tốt nhất nên uống nhiều nước lọc.</span></span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Ăn nhiều tinh bột</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Tinh bột là nhóm chất chính mà cơ thể chúng ta đưa vào cơ thể mỗi ngày, tuy nhiên nếu tinh bột vượt mức cho phép sẽ khiến lượng đường trong máu tăng cao đột khiến, khiến cơ thể tăng cân và tăng nguy cơ mắc bệnh tiểu đường.&nbsp;</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-weight: 700; font-family: KoHo;\">Giải pháp:</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">Ăn tinh bột vừa đủ, hạn chế ăn quá nhiều. Nên chuyển từ bánh mì trắng sang bánh mì ngũ cốc sẽ tốt hơn. Một số thực phẩm thay thế</span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"color: rgb(0, 0, 255); font-family: KoHo;\"><span style=\"outline-color: initial !important; outline-width: initial !important;\">tinh bột lành mạnh</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">như gạo lứt, yến mạch, diêm mạch,</span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"color: rgb(0, 0, 255); font-family: KoHo;\"><span style=\"outline-color: initial !important; outline-width: initial !important;\">kiều mạch</span></span><span style=\"font-family: KoHo;\">...</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><em><span style=\"font-size: 16px;\"><span style=\"font-family: KoHo;\">&gt;&gt;&gt; Tham khảo thêm:</span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"color: rgb(153, 204, 0); outline: none !important;\"><span style=\"font-weight: 700; font-family: KoHo;\">Nên ăn gì thay cơm trắng để tốt cho người bệnh tiểu đường</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">và đang giảm cân?</span></span></em></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Bỏ bữa sáng</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Nghe có vẻ vô lý nhưng thực tế các chuyên gia đã chỉ ra rằng việc bỏ bữa sáng sẽ làm tăng nguy cơ mắc bệnh tiểu đường. Nguyên nhân khi cơ thể bị đói sẽ tạo ra một phản ứng dây chuyền làm gián đoạn lượng insulin và kiểm soát lượng đường trong máu.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\"><img src=\"https://y5kbp0ifnvobj.vcdn.cloud/uploads/filecloud/2020/November/30/7164-718961606718687-1606718687.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-weight: 700; font-family: KoHo;\">Giải pháp:</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">Thay vì nhịn đói bạn nên ăn một bữa đơn giản nhanh gọn lẹ nhưng vẫn dinh dưỡng đủ với trứng, quả bơ và một ít loại hạt hay trái cây, ăn sáng với cách này giúp bạn ăn ít lại vào bữa trưa.</span></span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Ăn ít rau củ quả</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Chế độ ăn mỗi ngày là yếu tố quyết định không nhỏ đến sức khỏe của mỗi người. Một chế độ ăn ít rau củ quả không chỉ ảnh hưởng đến vóc dáng của bạn mà còn làm tăng nguy cơ mắc bệnh tiểu đường.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Các nghiên cứu đã chỉ ra rằng, có rất nhiều loại rau xanh giúp kiểm soát lượng đường trong máu và giảm cân hiệu quả ví dụ như rau bó xôi (bina), cà chua và bông cải xanh.&nbsp;</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-weight: 700; font-family: KoHo;\">Giải pháp:</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">Bổ sung rau xanh vào thực đơn hằng ngày bằng các món như salad, sup, canh, rau luộc chấm kho quẹt, rau xào, nộm… Nếu không thích rau, hãy thử với từng ít một, kẹp chúng vào sandwich chẳng hạn.</span></span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Không hoặc ít ăn cá</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Rất nhiều người chỉ thích ăn thịt, rất ít, thậm chí là không ăn cá. Thói quen xấu này không chỉ khiến cơ thể bạn mất đi một lượng axit béo omega-3 dồi dào tốt cho tim mạch và não bộ mà bên cạnh đó việc lười ăn cá, thay vào đó là thịt, tinh bột sẽ khiến bạn có nguy cơ cao mắc bệnh tim mạch.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\"><img src=\"https://y5kbp0ifnvobj.vcdn.cloud/uploads/filecloud/2020/November/30/7166-105891606718687-1606718687.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-weight: 700; font-family: KoHo;\">Giải pháp:</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">Cố gắng duy trì đều đặn ít nhất 2 bữa cá /tuần, ưu tiên các loại cá tốt cho sức khỏe như cá hồi, cá ngừ, cá thu…</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Ăn đêm nhiều</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Ăn đêm nhiều không chỉ khiến bạn nhanh lên cân mà còn làm tăng nguy cơ mắc bệnh tiểu đường.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-weight: 700; font-family: KoHo;\">Giải pháp:</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">Nên duy trì chế độ ăn 3 bữa đều đặn, đúng giờ mỗi ngày sẽ giúp bạn tránh ăn đêm. Nếu đói nên chọn các món ăn nhẹ như các loại hạt, trái cây kết hợp sữa chua.</span></span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Lo lắng, trầm cảm</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Trầm cảm ngày một nhiều khi nhịp sống càng hiện đại. Nhiều người không biết rằng mình đang bị trầm cảm và càng không biết rằng trầm cảm, lo lắng nhiều khiến cơ thể tăng nguy cơ mắc bệnh tiểu đường.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif; text-align: center;\"><span style=\"font-size: 16px;\"><span class=\"tceditor-image\"><img src=\"https://y5kbp0ifnvobj.vcdn.cloud/uploads/filecloud/2020/November/30/7165-251111606718687-1606718687.jpg\" alt=\"\" width=\"700\" height=\"500\" style=\"border: 0px; max-width: 100%;\"></span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"font-weight: 700;\"><span style=\"color: rgb(255, 0, 0); font-family: KoHo;\">Giải pháp:</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">Hãy xây dựng, sắp xếp cho mình chế độ làm việc, nghỉ ngơi, học tập, công việc gia đình, vui chơi cân đối. Đừng để bản thân bị áp lực quá nhiều và kéo dài.</span></span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Thức khuya</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Các nghiên cứu đã chỉ ra rằng, khi bạn ngủ ít hơn 6h mỗi ngày sẽ phá vỡ hormone kiểm soát lượng đường trong máu và tạo ra cảm giác đói dẫn đến tăng cân, tăng nguy cơ mắc bệnh tiểu đường.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-weight: 700; font-family: KoHo;\">Giải pháp:</span></span><span style=\"font-family: KoHo;\">&nbsp;</span><span style=\"font-family: KoHo;\">Dậy sớm thay vì ngủ muộn là cách hiệu quả để bạn giải quyết vấn đề công việc, học tập trong 1 ngày dài. Đôi khi bạn ngủ muộn vì trong ngày không tập trung xử lý hết công việc để tồn đến tối.</span></span></p><h2 style=\"font-family: latoregular, Helvetica, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 20px; margin-bottom: 10px; font-size: 30px;\"><span style=\"font-size: 19px;\"><span style=\"font-weight: 700; font-family: KoHo;\">Ăn uống theo cảm xúc</span></span></h2><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Khi buồn bạn ăn nhiều, vui bạn cũng ăn nhiều, đặc biệt là những lúc như thế này&nbsp; bạn lại cho cơ thể thỏa mãn nhu cầu, ví dụ như uống trà sữa, ăn đồ tinh bột nhiều. Cảm xúc quyết định đến thực đơn mỗi bữa khá nhiều. Điều này sẽ khiến bạn khó kiểm soát được cân nặng cũng như các căn bệnh mắc phải, ví dụ nhu tiểu đường.</span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-weight: 700;\"><span style=\"font-family: KoHo;\">Giải pháp:</span><span style=\"font-family: KoHo;\">&nbsp;</span></span></span><span style=\"font-family: KoHo;\">Nên xây dựng sẵn chế độ ăn hằng ngày khoa học, đôi khi bạn có thể chiều cho bản thân ăn ít đồ ngọt, tinh bột nhưng đừng để cảm xúc chi phối quá nhiều.</span></span></p><p style=\"margin-right: 0px; margin-bottom: 20px; margin-left: 0px; color: rgb(51, 51, 51); font-family: latoregular, Helvetica, sans-serif;\"><span style=\"font-size: 16px; font-family: KoHo;\">Bệnh tật thường bắt nguồn từ chế độ ăn uống và sinh hoạt của mỗi người, nhất là với bệnh tiểu đường. Chính vì vậy, để có một cơ thể khỏe mạnh, ngăn ngừa nguy cơ mắc bệnh tiểu đường, chớ coi thường những thói quen nhỏ cả nhà nhé</span></p>', '2020-12-19', '2', 2, '7163-814981606718687-1606718687.jpg', '9-thoi-quen-xau-lam-tang-nguy-co-mac-benh-tieu-duong', 1, 1, 'Tiểu đường', '2020-12-19 16:20:01', '2020-12-20 09:08:28'),
(5, 2, 'Cá hồi hữu cơ và 4 bí mật chỉ “chuyên gia” mới biết', '<p><span style=\"color: rgb(51, 51, 51); font-family: KoHo; font-size: 16px; background-color: rgb(249, 248, 244);\">Cá hồi vốn đã là thực phẩm bổ dưỡng không còn xa lạ. Ấy nhưng cũng là cá hồi, cá hồi hữu cơ lại được “các chuyên gia” ưu ái lựa chọn, bởi hơn ai hết chỉ người tiêu dùng hữu cơ - những người mà với Organica họ chính là những “chuyên gia” thông thái nhất mới hiểu rõ những bí mật đằng sau, những lợi ích vượt trội mà loại thực phẩm này mang lại.</span><br></p>', '<p class=\"MsoNormal\" style=\"margin: 15pt 0in 7.5pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Bí mật đằng sau độ “tươi rói” kích thích của\r\ncá hồi hữu cơ<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-bottom: 15pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Cá hồi hữu cơ “chinh\r\nphục” người tiêu dùng từ ánh nhìn đầu tiên bởi vẻ ngoài màu cam tươi rói, bắt\r\nmắt, khác hẳn với cá hồi thông thường. Một số người không biết lại auto dùng\r\nquan niệm cũ để áp đặt&nbsp;“chắc phải bơm, tiêm, ướp, tẩm chất gì thì mới\r\ntươi thế”&nbsp;. Nhưng sự thật chỉ những “chuyên gia” hữu cơ mới nắm rõ bí\r\nmật đằng sau độ tươi rói “hút hồn” và đánh thức thị giác ấy.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-bottom: 15pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Vì được nuôi 100%\r\ntrong môi trường tự nhiên với mật độ 10kg/m3 nên tất cả nguồn thức ăn của cá\r\nhồi hữu cơ đều có nguồn gốc tự nhiên, đó&nbsp; là những sinh vật biển như tôm,\r\ncôn trùng tự nhiên, tảo đỏ…&nbsp; Sau khi đạt ngưỡng, cá hồi được đánh bắt, sơ\r\nchế thành dạng fillet, cấp đông nhanh chóng để giữ trọn độ tươi ngon cùng giá\r\ntrị dinh dưỡng khi đến tay người tiêu dùng. Chính nguồn thức ăn tự nhiên cùng\r\nquy trình đánh bắt, sơ chế nhanh chóng, khoa học đã mang đến những miếng fillet\r\ncá hồi tươi rói, điều mà không phải ai cũng biết nếu không tìm hiểu.<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin: 15pt 0in 7.5pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">“Thịt cá chắc, dai, ngọt, thơm”<o:p></o:p></p><p class=\"MsoNormal\" style=\"margin-bottom: 15pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Không chỉ tươi rói\r\nchinh phục thị giác người nhìn, cá hồi hữu cơ “đánh bại” cá hồi thông thường\r\nqua xúc giác. Cụ thể khi dùng tay ấn mạnh vào từng thớ thịt, bạn sẽ thấy thịt\r\ncá hồi hữu cơ có độ đàn hồi cao, khi thả tay không để lại vết lõm trên bề mặt\r\ncá.<o:p></o:p></p><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Về vị của cá hồi hữu cơ, chỉ người ăn mới cảm nhận rõ, mỗi miếng\r\ncá có độ tươi, dai, thơm ngon, xuất sắc hơn khi chế biến dưới dạng Sashimi, vị\r\ncá béo ngậy tươi mát hòa quyện với nước chấm đậm đà, chút cay nồng nhớ mãi\r\nchẳng quên. Khi chế biến ở dạng áp chảo cá hồi hữu cơ mềm nhưng không ướt, khi\r\năn không ngấy, điều này là vì các mô liên kết của cá hồi hữu cơ, các vân mỡ xen\r\nđều đặn nên khi nấu chín từng hạt mỡ ngậy nhưng không hề ngấy.<o:p></o:p></p><h3 style=\"margin: 15pt 0in 7.5pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">“Cá\r\nhồi hữu cơ sở hữu giá trị dinh dưỡng vượt trội”<o:p></o:p></h3><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Nhìn bằng mắt thường, cá hồi hữu cơ sẽ\r\ntươi hơn cá hồi thường nhưng chắc chắn chẳng ai biết được sự khác biệt về giá\r\ntrị dinh dưỡng. Sự thật đằng sau những miếng fillet cá hồi hữu cơ là giá trị\r\ndinh dưỡng vượt trội, đặc biệt là hàm lượng Omega-3 gấp đôi so với cá hồi\r\nthường. Điều này được lý giải từ nguồn thức ăn hoàn toàn từ tự nhiên của cá hồi\r\nhữu cơ cũng như quy trình đánh bắt khi cá hồi đạt ngưỡng giá trị dinh dưỡng cao\r\nchứ không đánh bắt đồng loạt như cá hồi thường.<o:p></o:p></p><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Chính giá trị dinh dưỡng vượt trội giúp cá hồi phát huy tối đa\r\ncông dụng mang lại cho sức khỏe, điển hình như:<o:p></o:p></p><ul type=\"disc\">\r\n <li class=\"MsoNormal\" style=\"line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Giảm mỡ bụng, kiểm soát cân nặng<o:p></o:p></li>\r\n <li class=\"MsoNormal\" style=\"line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Ngăn ngừa lão hóa giúp bạn\r\n     trông trẻ hơn<o:p></o:p></li>\r\n <li class=\"MsoNormal\" style=\"line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Giảm viêm, giảm nguy cơ mắc các\r\n     bệnh mãn tính như tim mạch, tiểu đường, ung thư...<o:p></o:p></li>\r\n <li class=\"MsoNormal\" style=\"line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Bảo vệ sức khỏe não bộ, cải thiện\r\n     trí nhớ<o:p></o:p></li>\r\n</ul><h3 style=\"margin: 15pt 0in 7.5pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">“Lựa\r\nchọn hàng đầu cho sức khỏe cả gia đình”<o:p></o:p></h3><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Điều cốt lõi khiến người tiêu dùng tìm\r\nđến thực phẩm hữu cơ nói chung chính là mức độ an toàn, giúp mỗi người, nhất là\r\nngười nội trợ an tâm lựa chọn thực phẩm cho cả gia đình. Cá hồi hữu cơ nhà GreenFresh\r\nkhác cá hồi thường, điểm chính cũng nằm ở đây.<o:p></o:p></p><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Để đảm bảo tiêu chí an toàn, đòi hỏi cá\r\nhồi hữu cơ phải tuân thủ quy trình nuôi trồng nghiêm ngặt, đáp ứng các tiêu chí\r\nkhắt khe đến từ tổ chức hữu cơ. Sau 5 năm nghiên cứu, thực hành, tuân thủ một\r\ncách nghiêm túc về Nông nghiệp, thủy sản bền vững, cá hồi thương hiệu Mùa tại\r\nNauy, sản phẩm đang phân phối tại nhà GreenFresh đã đạt chứng nhận hữu cơ của\r\nOrganic Certified - Debio (một trong những chứng nhận khắt khe và khó khăn nhất\r\ntrong sản xuất sản phẩm theo hướng bền vững).<o:p></o:p></p><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Theo đó, cá hồi hữu cơ tại GreenFresh\r\nđược nuôi 100% trong môi trường tự nhiên, thức ăn hoàn toàn tự nhiên, không\r\nbiến đổi gen và được kiểm soát bởi MSC. Đặc biệt quy trình nuôi trồng cá hồi\r\nhữu cơ không có sự can thiệp của hóa chất bao gồm hóa chất để làm sạch bề mặt\r\nlưới chuồng nuôi, hóa chất trong quá trình nuôi như kháng sinh (để đảm bảo sức\r\nkhỏe cho cá hồi, quy trình nuôi hữu cơ áp dụng các loại thiên địch để tiêu diệt\r\nrận biển - được xem là kẻ thù của cá hồi), hóa chất trong quá trình bảo quản…<o:p></o:p></p><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Chính quy trình nghiêm ngặt này, giúp cá\r\nhồi Mùa tại nhà GreenFresh không chỉ đảm bảo an toàn, giá trị dinh dưỡng vượt\r\ntrội mà hương vị cũng thơm ngon khác lạ, thịt cá chắc, tươi ngon, béo thơm trên\r\ntừng miếng cắn.<o:p></o:p></p><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Mặc dù chỉ mới “cập\r\nbến” được vài ngày nhưng&nbsp;<a href=\"https://www.organica.vn/thuy-hai-san/fillet-ca-hoi-mua\" style=\"outline: none !important;\">cá hồi&nbsp;</a>tại nhà GreenFresh đã được các anh/chị khách yêu thích lựa chọn.\r\nHôm rồi nhà O nghe được một đoạn trò chuyện giữa 2 chị khách như thế này. “Thử\r\nđi, “đáng đồng tiền bát gạo” lắm đó” - Lời của 1 chị khách giới thiệu cho 1 chị\r\nkhách khác đang cầm trên tay 1 vỉ cá hồi nhà GreenFresh”<o:p></o:p></p><p style=\"margin: 0in 0in 15pt; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Trong nhiều năm kinh doanh thực phẩm hữu\r\ncơ, GreenFresh thấy rằng, mọi chiến dịch Marketing quảng bá sẽ chẳng thể nào\r\nhiệu quả bằng chính những lời “review” chân thật đến từ những người tiêu dùng,\r\nnhững người mà GreenFresh luôn gọi là “chuyên gia” hữu cơ đời thường, bởi chính\r\nhọ mới là người thưởng thức và cảm nhận rõ nhất giá trị mà thực phẩm hữu cơ\r\nmang lại.<o:p></o:p></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\"><o:p>&nbsp;</o:p></p>', '2020-12-19', '2', 2, '7249-446881607915672-1607915672.jpg', 'ca-hoi-huu-co-va-4-bi-mat-chi-chuyen-gia-moi-biet', 1, 1, 'cá hồi, hữu cơ, sức khỏe, gia đình', '2020-12-19 16:20:48', '2020-12-19 16:20:48');
